--// http://www.sqlines.com/online--// Tabla de usuarios y autoridades por defecto de Spring Security-- Crear tabla de usuariosCREATE TABLE users (	[username] varchar(50) NOT NULL,	[password] varchar(50) NOT NULL,	[enabled] smallint NOT NULL,	PRIMARY KEY ([username])) ;-- Crear tabla de rolesCREATE TABLE authorities (	[username] varchar(50) NOT NULL,	[authority] varchar(50) NOT NULL,	CONSTRAINT [authorities_idx_1] UNIQUE  ([username],[authority]),	CONSTRAINT [authorities_ibfk_1]	FOREIGN KEY ([username])	REFERENCES users ([username])) ;-- Insertamos nuestros usuariosINSERT INTO users VALUES ('luis','{noop}luis123',1);INSERT INTO users VALUES ('marisol','{noop}mari123',1);-- Insertamos (asignamos roles) a nuestros usuarios.INSERT INTO authorities VALUES ('luis','EDITOR');INSERT INTO authorities VALUES ('marisol','EDITOR');INSERT INTO authorities VALUES ('marisol','MANAGER');